Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
GameTooltip:SetScale(CurrencyTracking.db.profile.tooltip_scale);
GameTooltip:SetBackdropColor(0, 0, 0, CurrencyTracking.db.profile.tooltip_alpha);
if (self.tooltipText) then
	GameTooltip:SetText(self.tooltipText);
elseif (self:GetParent().isItem) then
	local name = self:GetParent().itemName;
	GameTooltip:SetText(name, 1, 1, 1, nil, false);
end

Scripts.OnLoad:
local name = self:GetName();
self.count = _G[name.."Count"];
self.icon = _G[name.."Icon"];
self.highlight = _G[name.."Highlight"];
self:RegisterForDrag("LeftButton");
self:RegisterForClicks("LeftButtonDown", "RightButtonDown");
