Scripts.OnMouseUp:
if ( button == "LeftButton" ) then
	PlaySound(SOUNDKIT.IG_MAINMENU_OPTION_CHECKBOX_ON);
	self:GetParent():GetParent():GetParent():SelectSetFromButton(self.setID);
elseif ( button == "RightButton" ) then
	local dropDown = self:GetParent():GetParent().FavoriteDropDown;
	dropDown.baseSetID = self.setID;
	ToggleDropDownMenu(1, nil, dropDown, self, 0, 0);
	PlaySound(SOUNDKIT.IG_MAINMENU_OPTION_CHECKBOX_ON);
end

Scripts.OnLeave:
if ( not self:GetParent():IsMouseOver() ) then
	self:Hide();
end

Scripts.OnFinished:
local frame = self:GetParent();
frame:Hide();
local slotButton = frame:GetParent();
WardrobeTransmogFrame_UpdateSlotButton(slotButton);

local frame = self:GetParent();
frame:Hide();
local slotButton = frame:GetParent();
WardrobeTransmogFrame_UpdateSlotButton(slotButton);

Scripts.OnUpdate:
if ( self:IsMouseOver() ) then
	self:SetAlpha(0);
else
	self:SetAlpha(abs(mod(GetTime(), 2) - 1));
end

Scripts.OnEnter:
if ( not self:IsEnabled() ) then
	GameTooltip:ClearAllPoints();
	GameTooltip:SetPoint("BOTTOM", self, "TOP", 0, 0);
	GameTooltip:SetOwner(self, "ANCHOR_PRESERVE");
	GameTooltip:SetText(WARDROBE_NO_SEARCH);
end
